<?phpclass Admin_PhiendauchinhsuaController extends App_Controller_AdminController {    public function init(){        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction()	{	   if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 0;		$daugia = App_Models_DaugiaModel::getInstance();		$idpd = $_GET["idpd"];		$status = @$_GET["st"];		$this->view->status = $status;				$sql = "Select * From ishali_bid_phiendau where idpd = '". $idpd ."'";		$data = $daugia->ThucThiTruyVan($sql);				$this->view->phiendauchitiet = $data;				$TGBD = $data[0]["tgbatdau"];		$TGBatDau = $daugia->TachThoiGian($TGBD);		$this->view->TGBatDau = $TGBatDau;				$TGKT = $data[0]["tgketthuc"];		$TGKetThuc = $daugia->TachThoiGian($TGKT);		$this->view->TGKetThuc = $TGKetThuc;						$sql = "Select idsp, tensp From ishali_bid_sanpham order by idsp desc";		$data = $daugia->ThucThiTruyVan($sql);				$this->view->sanpham = $data;    }		public function capnhatphiendauAction()	{		$daugia = App_Models_DaugiaModel::getInstance();				$idPD = @$_POST["idpd"];		$tenPD = @$_POST["tenpd"];		$idSP = @$_POST["idsp"];		$GiaBan = @$_POST["giaban"];		$GiaKhoiDiem = @$_POST["giakhoidiem"];		$BuocGia = @$_POST["buocgia"];				$NgayBatDau = @$_POST["ngaybatdau"];		$GioBatDau = @$_POST["giobatdau"];		$PhutBatDau = @$_POST["phutbatdau"];				$NgayKetThuc = @$_POST["ngayketthuc"];		$GioKetThuc = @$_POST["gioketthuc"];		$PhutKetThuc = @$_POST["phutketthuc"];				$ThoiGianBatDau = $daugia->GomThoiGian($NgayBatDau, $GioBatDau, $PhutBatDau, '00');		$ThoiGianKetThuc = $daugia->GomThoiGian($NgayKetThuc, $GioKetThuc, $PhutKetThuc, '59');						$sql  = "Update ishali_bid_phiendau Set ";		$sql .= "idsp = '". $idSP ."', ";		$sql .= "tenpd = '". $tenPD ."', ";		$sql .= "giaban = '". $GiaBan ."', ";		$sql .= "giakhoidiem = '". $GiaKhoiDiem ."', ";		$sql .= "buocgia = '". $BuocGia ."', ";		$sql .= "tgbatdau = '". $ThoiGianBatDau ."', ";		$sql .= "tgketthuc = '". $ThoiGianKetThuc ."' ";		$sql .= "Where idpd = '". $idPD ."'";				$data = $daugia->InsertDeleteUpdateQuery($sql);		if($data == 1)		{			echo "<script>ThongBaoDongY('Lưu Thành Công.', taaa.appdomain+'/admin/phiendauchinhsua?st=1&idpd=$idPD');</script>";			}		else		{			echo "<script>ThongBaoDongY('Lưu không thành công<br/>Vui Lòng thực hiện lại thao tác.', taaa.appdomain+'/admin/phiendauchinhsua?st=1&idpd=$idPD');</script>";		}    }	}