<?phpclass Admin_PhiendausapdienraController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction()	{		$daugia = App_Models_DaugiaModel::getInstance();		if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 1;		if($this->_request->getParam("idpage") != "")        {			$idpagee = $this->_request->getParam("idpage");			$_SESSION['idpage'] = $idpagee;		}		@$idpage = $_SESSION['idpage'];				$checkSessionIdpage = $daugia->KiemTraSessionIdPage($idpage);		if($checkSessionIdpage == 0)		{			$this->view->checkSessionIdpage = $checkSessionIdpage;		}		else		{			$datenow = date("Y-m-d H:i:s");			$sql = "Select idpd, tenpd, tensp, tgbatdau, tgketthuc From ishali_bid_phiendau pd, ishali_bid_sanpham sp where pd.idpage = '". $idpage ."' and pd.idsp = sp.idsp and tgbatdau > '" .$datenow. "' Order By idpd DESC";			$data = $daugia->ThucThiTruyVan($sql);			//print_r($data);			$this->view->phiendau = $data;			$this->view->checkSessionIdpage = $checkSessionIdpage;		}    }		public function xoaphiendauAction()	{		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();						$idpd = $_GET["idpd"];				$sql = "Delete from ishali_bid_phiendau where idpd = '". $idpd ."'";		$daugia -> ThucThiTruyVan($sql);		header("Location: ../phiendaudangdienra?result=3");	}		public function xoaphiendaucuAction()	{		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();				$idpd = $_GET["idpd"];				$sql = "Delete from ishali_bid_phiendau where idpd = '". $idpd ."'";		$daugia -> ThucThiTruyVan($sql);		header("Location: ../phiendauketthuc?result=3");	}	}