<?phpclass Admin_SanphamdaugiaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }        $daugia = App_Models_DaugiaModel::getInstance();				$sql = "Select idsp, tensp, gia, urlhinh from ishali_bid_sanpham";				$data = $daugia->ThucThiTruyVan($sql);		$this->view->sanpham = $data;    }		public function xoasanphamAction() {		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();				$daugia = App_Models_DaugiaModel::getInstance();				$idSP = $_GET["idsp"];		$sql = "Select urlhinh from ishali_bid_sanpham where idsp = '". $idSP ."'";		$data = $daugia -> ThucThiTruyVan($sql);				$UrlHinh = $data[0]["urlhinh"];		$UrlHinh2 = APPLICATION_PATH ."/layouts/tmpdaugia/images/upload/images/" . $UrlHinh;		if(file_exists($UrlHinh2)) unlink($UrlHinh2);				$sql = "Delete from ishali_bid_sanpham where idsp = '". $idSP ."'";		$daugia -> ThucThiTruyVan($sql);		header("Location: ../sanphamdaugia?result=3");	}}