<?phpclass Admin_SanphamdaugiaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }				$_SESSION['list_page'] = 1;		$daugia = App_Models_DaugiaModel::getInstance();				if($this->_request->getParam("idpage") != "")        {			$idpagee = $this->_request->getParam("idpage");			$_SESSION['idpage'] = $idpagee;		}		@$idpage = $_SESSION['idpage'];				$checkSessionIdpage = $daugia->KiemTraSessionIdPage($idpage);		if($checkSessionIdpage == 0)		{			$this->view->checkSessionIdpage = $checkSessionIdpage;		}		else		{			$sql = "Select idsp, tensp, gia, urlhinh from ishali_bid_sanpham where idpage = '". $idpage ."'";						$data = $daugia->ThucThiTruyVan($sql);			$this->view->sanpham = $data;						$sql = "select donvitien, thongtinsp from ishali_config where idpage = '". $idpage ."'";				$data = $daugia->SelectQuery($sql);				if(count($data) == 0)				{					$donvitien = "VNĐ";					$thongtinsp = 0;				}				else				{					if($data[0]['donvitien'] == "")						$donvitien = "VNĐ";					else						$donvitien = $data[0]['donvitien'];											if($data[0]['thongtinsp'] == "")						$thongtinsp = 0;					else						$thongtinsp = $data[0]['thongtinsp'];				}				$this->view->donvitien = $donvitien;				$this->view->thongtinsp = $thongtinsp;				$this->view->checkSessionIdpage = $checkSessionIdpage;		}    }		public function xoasanphamAction() {		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();				$daugia = App_Models_DaugiaModel::getInstance();				$idSP = $_GET["idsp"];		$sql = "Select urlhinh from ishali_bid_sanpham where idsp = '". $idSP ."'";		$data = $daugia -> ThucThiTruyVan($sql);				$UrlHinh = $data[0]["urlhinh"];		$UrlHinh2 = APPLICATION_PATH ."/layouts/tmpdaugia/images/upload/images/" . $UrlHinh;		if(file_exists($UrlHinh2)) unlink($UrlHinh2);				$sql = "Delete from ishali_bid_sanpham where idsp = '". $idSP ."'";		$daugia -> ThucThiTruyVan($sql);		header("Location: ../sanphamdaugia?result=3");	}}