<?phpclass Admin_TaophiendaugiaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction()	{if(!isset($this->_SESSION->iduseradmin))		$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        $daugia = App_Models_DaugiaModel::getInstance();				$sql = "Select idsp, tensp from ishali_bid_sanpham";		$data = $daugia -> ThucThiTruyVan($sql);		$this->view->thongtinsp = $data;    }		public function xulytaophiendaugiaAction()	{		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();				$daugia = App_Models_DaugiaModel::getInstance();				$tenPD = @$_POST["tenpd"];		$idSP = @$_POST["idsp"];		$GiaBan = @$_POST["giaban"];		$GiaKhoiDiem = @$_POST["giakhoidiem"];		$BuocGia = @$_POST["buocgia"];		//$ThoiGianBatDau = @$_POST["thoigianbatdau"];		//$ThoiGianKetThuc = @$_POST["thoigianketthuc"];				$NgayBatDau = @$_POST["ngaybatdau"];		$GioBatDau = @$_POST["giobatdau"];		$PhutBatDau = @$_POST["phutbatdau"];				$NgayKetThuc = @$_POST["ngayketthuc"];		$GioKetThuc = @$_POST["gioketthuc"];		$PhutKetThuc = @$_POST["phutketthuc"];				$ThoiGianBatDau = $daugia->GomThoiGian($NgayBatDau, $GioBatDau, $PhutBatDau, '00');		$ThoiGianKetThuc = $daugia->GomThoiGian($NgayKetThuc, $GioKetThuc, $PhutKetThuc, '59');				$sql  = "Insert into ishali_bid_phiendau Values (";		$sql .= "'null', ";		$sql .= "'". $idSP ."', ";		$sql .= "'". $tenPD ."', ";		$sql .= "'". $GiaBan ."', ";		$sql .= "'". $GiaKhoiDiem ."', ";		$sql .= "'". $BuocGia ."', ";		$sql .= "'". $ThoiGianBatDau ."', ";		$sql .= "'". $ThoiGianKetThuc ."') ";				$kq = $daugia -> ThucThiTruyVan($sql);		header("Location: ../taophiendaugia?result=1");	}	}