<?phpclass Admin_ThongbaoController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$daugia = App_Models_DaugiaModel::getInstance();		$_SESSION['list_page'] = 1;		if($this->_request->getParam("idpage") != "")        {			$idpagee = $this->_request->getParam("idpage");			$_SESSION['idpage'] = $idpagee;		}		@$idpage = $_SESSION['idpage'];		$checkSessionIdpage = $daugia->KiemTraSessionIdPage($idpage);		if($checkSessionIdpage == 0)		{			$this->view->checkSessionIdpage = $checkSessionIdpage;		}		else		{			$sql = "Select baiviet from ishali_bid_baiviet where idbv = 4 and idpage = '". $idpage ."'";			$data = $daugia->ThucThiTruyVan($sql);			if(count($data)>0)				$this->view->thongbao = $data[0]["baiviet"];			else				$this->view->thongbao = "";			$this->view->checkSessionIdpage = $checkSessionIdpage;		}    }        public function chinhsuathongbaoAction(){		$daugia = App_Models_DaugiaModel::getInstance();		$idpage = $_SESSION['idpage'];		$ThongBao = @$_POST["thongbao"];				$sql = "Select 1 from ishali_bid_baiviet where idbv = 4 and idpage = '". $idpage ."'";		$data = $daugia->SelectQuery($sql);		if(count($data)>0)		{			$sql = "Update ishali_bid_baiviet Set baiviet = '$ThongBao' Where idbv = 4 and idpage = '". $idpage ."'";		}		else		{			$sql = "Insert into ishali_bid_baiviet(idbv, baiviet, idpage) value ('4', '". $ThongBao ."', '". $idpage ."')";		}		//echo $sql;		$data = $daugia->InsertDeleteUpdateQuery($sql);		if($data == 1)		{			echo "<script>ThongBaoDongY('Lưu Thành Công.', taaa.appdomain+'/admin/thongbao');</script>";			}		else		{			echo "<script>ThongBaoDongY('Lưu không thành công<br/>Vui Lòng thực hiện lại thao tác.', taaa.appdomain+'/admin/thongbao');</script>";		}    }	}