<?phpclass Admin_UserController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }        $daugia = App_Models_DaugiaModel::getInstance();				$sql = "Select * From ishali_bid_user order by iduser desc";				$user = $daugia-> ThucThiTruyVan($sql);		$this->view->user = $user;    }}