<?phpclass Admin_UserController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {		$daugia = App_Models_DaugiaModel::getInstance();        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 1;		if($this->_request->getParam("idpage") != "")        {			$idpagee = $this->_request->getParam("idpage");			$_SESSION['idpage'] = $idpagee;		}		@$idpage = $_SESSION['idpage'];				$checkSessionIdpage = $daugia->KiemTraSessionIdPage($idpage);		if($checkSessionIdpage == 0)		{			$this->view->checkSessionIdpage = $checkSessionIdpage;		}		else		{			$sql = "Select * From ishali_bid_user where idpage = '". $idpage ."' order by iduser desc";			$user = $daugia-> ThucThiTruyVan($sql);			$this->view->user = $user;			$this->view->checkSessionIdpage = $checkSessionIdpage;		}    }}