<?phpclass Admin_UserchitietController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {                if(!isset($this->_SESSION->iduseradmin))		$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        $daugia = App_Models_DaugiaModel::getInstance();		$idUser = @$_GET["iduser"];				$sql = "Select * From ishali_bid_user where iduser = '" . $idUser . "'";				$user = $daugia->ThucThiTruyVan($sql);		$this->view->user = $user;    }		public function deleteAction() {        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();		$idUser = @$_GET["iduser"];				$sql = "Delete from ishali_bid_user where iduser = '" . $idUser . "'";				$user = $daugia->ThucThiTruyVan($sql);		$this->view->user = $user;				header("Location: ../user?result=3");    }	}