<?phpclass Admin_AdmindaugiaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 0;    }		public function xulythemsanphamAction() {		$daugia = App_Models_DaugiaModel::getInstance();		$idpage = $_SESSION['idpage'];		$TenSP = $_POST["tensp"];		$GiaBan = $_POST["giaban"];		$file = @$_FILES["uploadhinhanh"];		$UrlHinh = @$file['name'];				$UrlHinh2 = APPLICATION_PATH ."/layouts/tmpdaugia/images/upload/images/" . $UrlHinh;		move_uploaded_file($file['tmp_name'],$UrlHinh2);				$TitleFB = @$_POST["titlefb"];        $MetaFB = @$_POST["metafb"];				//INSERT INTO ishali_bid_sanpham (tensp, gia, urlhinh, titlechiase, motachiase, idpage) VALUES ('1', '2', '', '3', '4', '356730004423499')		$sql  = "Insert into ishali_bid_sanpham (tensp, gia, urlhinh, titlechiase, motachiase, idpage) Values (";		$sql .= "'". $TenSP ."', ";		$sql .= "'". $GiaBan ."', ";		$sql .= "'". $UrlHinh ."', ";        $sql .= "'". $TitleFB ."', ";		$sql .= "'". $MetaFB ."', ";		$sql .= "'". $idpage ."')";		$kq = $daugia -> InsertDeleteUpdateQuery($sql);		if($kq == 1)		{			echo "<script>ThongBaoDongY('Lưu Thành Công.', taaa.appdomain+'/admin/sanphamdaugia');</script>";			}		else		{			echo "<script>ThongBaoDongY('Lưu không thành công<br/>Vui Lòng thực hiện lại thao tác.', taaa.appdomain+'/admin/sanphamdaugia');</script>";		}		//header("Location: ../admindaugia?result=1");		    }		public function taophiendauAction() {        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();				$daugia = App_Models_DaugiaModel::getInstance();				echo 'Tao phien dau';		}		}