<?phpclass Admin_GioithieuController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");		$daugia = App_Models_DaugiaModel::getInstance();                $sql = "Select baiviet from ishali_bid_baiviet where idbv = 3";        $data = $daugia->ThucThiTruyVan($sql);        $this->view->gioithieu = $data[0]["baiviet"];    }        public function chinhsuagioithieuAction(){        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		        $daugia = App_Models_DaugiaModel::getInstance();	        $Gioithieu = @$_POST["gioithieu"];        $sql = "Update ishali_bid_baiviet Set baiviet = '$Gioithieu' Where idbv = 3";            $data = $daugia->ThucThiTruyVan($sql);        header("Location: ../gioithieu?result=2");    }	}