<?phpclass Admin_LoginadminController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {		$daugia = App_Models_DaugiaModel::getInstance();                   }        public function xulyloginAction() {        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();                $UserAdmin = $_POST["useradmin"];        $PasswordAdmin = sha1($_POST["passadmin"]);                $sql = "Select iduser, hoten From ishali_bid_useradmin where useradmin = '".$UserAdmin."' and password = '".$PasswordAdmin."'";        $data = $daugia->ThucThiTruyVan($sql);                $this->_SESSION->iduseradmin=$data[0]["iduser"];        $this->_SESSION->hotenadmin=$data[0]["hoten"];        echo count($data);           }        public function xulydangxuatAction() {        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();        unset ($this->_SESSION->iduseradmin);        unset ($this->_SESSION->hotenadmin);        //echo 'aaaaaaaaaaaa';    }}