<?phpclass Admin_PhiendauchinhsuaController extends App_Controller_AdminController {    public function init(){        parent::init();    }    public function indexAction()	{		$daugia = App_Models_DaugiaModel::getInstance();		$idpd = $_GET["idpd"];		$status = @$_GET["st"];		$this->view->status = $status;				$sql = "Select * From ishali_bid_phiendau where idpd = '". $idpd ."'";		$data = $daugia->ThucThiTruyVan($sql);				$this->view->phiendauchitiet = $data;				$sql = "Select idsp, tensp From ishali_bid_sanpham order by idsp desc";		$data = $daugia->ThucThiTruyVan($sql);				$this->view->sanpham = $data;    }		public function capnhatphiendauAction()	{		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();				echo $idPD = @$_POST["idpd"];		echo $tenPD = @$_POST["tenpd"];		echo $idSP = @$_POST["idsp"];		echo $GiaBan = @$_POST["giaban"];		echo $GiaKhoiDiem = @$_POST["giakhoidiem"];		echo $BuocGia = @$_POST["buocgia"];		echo $ThoiGianBatDau = @$_POST["thoigianbatdau"];		echo $ThoiGianKetThuc = @$_POST["thoigianketthuc"];				$sql  = "Update ishali_bid_phiendau Set ";		$sql .= "idsp = '". $idSP ."', ";		$sql .= "tenpd = '". $tenPD ."', ";		$sql .= "giaban = '". $GiaBan ."', ";		$sql .= "giakhoidiem = '". $GiaKhoiDiem ."', ";		$sql .= "buocgia = '". $BuocGia ."', ";		$sql .= "tgbatdau = '". $ThoiGianBatDau ."', ";		$sql .= "tgketthuc = '". $ThoiGianKetThuc ."' ";		$sql .= "Where idpd = '". $idPD ."'";				$data = $daugia->ThucThiTruyVan($sql);		header("Location: ../phiendauchinhsua?result=2&idpd=".$idPD);    }	}