<?phpclass Admin_PhiendauketthucController extends App_Controller_AdminController {    public function init() {        parent::init();         $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction()	{	   if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$daugia = App_Models_DaugiaModel::getInstance();        $datenow = date("Y-m-d H:i:s");		$sql = "Select idpd, tenpd, tensp, tgbatdau, tgketthuc From ishali_bid_phiendau pd, ishali_bid_sanpham sp where pd.idsp = sp.idsp and tgketthuc < '" .$datenow. "' Order By idpd DESC";		$data = $daugia->ThucThiTruyVan($sql);		//print_r($data);		$this->view->phiendau = $data;    }		public function xoaphiendauAction()	{		$this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		$daugia = App_Models_DaugiaModel::getInstance();						$idpd = $_GET["idpd"];				$sql = "Delete from ishali_bid_phiendau where idpd = '". $idpd ."'";		$daugia -> ThucThiTruyVan($sql);		header("Location: ../phiendaudangdienra?result=3");	}	}