<?phpclass Admin_SanphamchitietController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 0;        $daugia = App_Models_DaugiaModel::getInstance();				$idSP = @$_GET["idsp"];				$sql = "Select * from ishali_bid_sanpham where idsp = ". $idSP;				$data = $daugia->ThucThiTruyVan($sql);		$this->view->sanphamchitiet = $data;				    }		public function chinhsuasanphamAction() {		$daugia = App_Models_DaugiaModel::getInstance();				$idSP = $_POST["idsp"];		$TenSP = $_POST["tensp"];		$GiaBan = $_POST["giaban"];        $TitleFB = @$_POST["titlefb"];        $MetaFB = @$_POST["metafb"];                		$file = @$_FILES["uploadhinhanh"];		if($file["name"] !="")		{			$UrlHinh = @$file['name'];						$UrlHinh2 = APPLICATION_PATH ."/layouts/tmpdaugia/images/upload/images/" . $UrlHinh;			move_uploaded_file($file['tmp_name'],$UrlHinh2);						$sql  = "Update ishali_bid_sanpham Set ";			$sql .= "tensp = '". $TenSP ."', ";			$sql .= "gia = '". $GiaBan ."', ";			$sql .= "urlhinh = '". $UrlHinh ."', ";            $sql .= "titlechiase = '". $TitleFB ."', ";			$sql .= "motachiase = '". $MetaFB ."' ";                        			$sql .= "Where idsp = '". $idSP ."'";						$sqlha = "Select urlhinh from ishali_bid_sanpham where idsp = '" . $idSP . "'";			$ha = $daugia -> ThucThiTruyVan($sqlha);			$UrlHinhOld = APPLICATION_PATH ."/layouts/tmpdaugia/images/upload/images/" . $ha[0]["urlhinh"];			if(file_exists($UrlHinhOld)) unlink($UrlHinhOld);					}		else		{			$sql  = "Update ishali_bid_sanpham Set ";			$sql .= "tensp = '". $TenSP ."', ";			$sql .= "gia = '". $GiaBan ."', ";            $sql .= "titlechiase = '". $TitleFB ."', ";			$sql .= "motachiase = '". $MetaFB ."' ";			$sql .= "Where idsp = '". $idSP ."'";		}		$data = $daugia -> InsertDeleteUpdateQuery($sql);		if($data == 1)		{			echo "<script>ThongBaoDongY('Lưu Thành Công.', taaa.appdomain+'/admin/sanphamdaugia');</script>";			}		else		{			echo "<script>ThongBaoDongY('Lưu không thành công<br/>Vui Lòng thực hiện lại thao tác.', taaa.appdomain+'/admin/sanphamdaugia');</script>";		}	}			}