<?phpclass Admin_SanphamsuaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))			header("Location:loginadmin");        $daugia = App_Models_DaugiaModel::getInstance();				$idSP = @$_GET["idsp"];				$sql = "Select * from ishali_bid_sanpham where idsp = ". $idSP;				$data = $daugia->ThucThiTruyVan($sql);		$this->view->sanphamchitiet = $data;				    }	}