<?phpclass Admin_TaophiendaugiaController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction()	{		if(!isset($this->_SESSION->iduseradmin))		{			$link = APP_DOMAIN .'/admin/loginadmin';			header("Location:$link");        }		$_SESSION['list_page'] = 0;        $daugia = App_Models_DaugiaModel::getInstance();		$idpage = $_SESSION['idpage'];		$sql = "Select idsp, tensp from ishali_bid_sanpham where idpage = '". $idpage ."'";		$data = $daugia -> ThucThiTruyVan($sql);		$this->view->thongtinsp = $data;    }		public function xulytaophiendaugiaAction()	{		$daugia = App_Models_DaugiaModel::getInstance();		$idpage = $_SESSION['idpage'];				$tenPD = @$_POST["tenpd"];		$idSP = @$_POST["idsp"];		$GiaBan = @$_POST["giaban"];		$GiaKhoiDiem = @$_POST["giakhoidiem"];		$BuocGia = @$_POST["buocgia"];				$NgayBatDau = @$_POST["ngaybatdau"];		$GioBatDau = @$_POST["giobatdau"];		$PhutBatDau = @$_POST["phutbatdau"];				$NgayKetThuc = @$_POST["ngayketthuc"];		$GioKetThuc = @$_POST["gioketthuc"];		$PhutKetThuc = @$_POST["phutketthuc"];				$ThoiGianBatDau = $daugia->GomThoiGian($NgayBatDau, $GioBatDau, $PhutBatDau, '00');		$ThoiGianKetThuc = $daugia->GomThoiGian($NgayKetThuc, $GioKetThuc, $PhutKetThuc, '59');				$sql  = "Insert into ishali_bid_phiendau ";		$sql .= "(idsp, tenpd, giaban, giakhoidiem, buocgia, tgbatdau, tgketthuc, idpage) Values (";		$sql .= "'". $idSP ."', ";		$sql .= "'". $tenPD ."', ";		$sql .= "'". $GiaBan ."', ";		$sql .= "'". $GiaKhoiDiem ."', ";		$sql .= "'". $BuocGia ."', ";		$sql .= "'". $ThoiGianBatDau ."', ";		$sql .= "'". $ThoiGianKetThuc ."', ";		$sql .= "'". $idpage ."') ";				$kq = $daugia -> InsertDeleteUpdateQuery($sql);		//header("Location: ../taophiendaugia?result=1");		if($kq == 1)		{			echo "<script>ThongBaoDongY('Lưu Thành Công.', taaa.appdomain+'/admin/taophiendaugia');</script>";			}		else		{			echo "<script>ThongBaoDongY('Lưu không thành công<br/>Vui Lòng thực hiện lại thao tác.', taaa.appdomain+'/admin/taophiendaugia');</script>";		}	}	}