<?phpclass Admin_ThongbaoController extends App_Controller_AdminController {    public function init() {        parent::init();        $this->_SESSION=new Zend_Session_Namespace();    }    public function indexAction() {        if(!isset($this->_SESSION->iduseradmin))			header("Location:loginadmin");		$daugia = App_Models_DaugiaModel::getInstance();                $sql = "Select baiviet from ishali_bid_baiviet where idbv = 4";        $data = $daugia->ThucThiTruyVan($sql);        $this->view->thongbao = $data[0]["baiviet"];    }        public function chinhsuathongbaoAction(){        $this->_helper->viewRenderer->setNoRender(true);		$this->_helper->layout->disableLayout();		        $daugia = App_Models_DaugiaModel::getInstance();	        $ThongBao = @$_POST["thongbao"];        echo $sql = "Update ishali_bid_baiviet Set baiviet = '$ThongBao' Where idbv = 4";            $data = $daugia->ThucThiTruyVan($sql);        header("Location: ../thongbao?result=2");    }	}